mapper.update:
UPDATE drug_user_info
SET version=#{drugUserInfo.version}
WHERE id=#{drugUserInfo.id}

UPDATE drug_user_info
SET user_id=#{drugUserInfo.userId},drug_user_name=#{drugUserInfo.drugUserName},drug_user_relation=#{drugUserInfo.drugUserRelation},drug_user_idCard=#{drugUserInfo.drugUserIdCard},
gender=#{drugUserInfo.gender},phone=#{drugUserInfo.phone},allergy_history=#{drugUserInfo.allergyHistory},family_history=#{drugUserInfo.familyHistory},liver_function=#{drugUserInfo.liverFunction},
renal_function=#{drugUserInfo.renalFunction},pregnant_lactation=#{drugUserInfo.pregnantLactation},birthday=#{drugUserInfo.birthday}
WHERE id=#{drugUserInfo.id} and version=#{drugUserInfo.version}

mapper.select:
SELECT id,version
FROM drug_user_info
WHERE id=#{id}
